import React, { Component } from "react";
import GuestDataService from "../services/guest.service";
import { Link } from "react-router-dom";

export default class TutorialsList extends Component {
  constructor(props) {
    super(props);
    this.onChangeSearchGuest = this.onChangeSearchGuest.bind(this);
    this.retrieveGuest = this.retrieveGuest.bind(this);
    this.refreshList = this.refreshList.bind(this);
    this.setActiveGuest = this.setActiveGuest.bind(this);
 //   this.removeAllTutorials = this.removeAllTutorials.bind(this);
    this.searchGuest = this.searchGuest.bind(this);

    this.state = {
      guests: [],
      currentGuest: null,
      currentIndex: -1,
      searchGuest: ""
    };
  }

  componentDidMount() {
    this.retrieveGuest();
  }

  onChangeSearchGuest(e) {
    const searchGuest = e.target.value;

    this.setState({
      searchGuest: searchGuest
    });
  }

  retrieveGuest() {
    GuestDataService.getAll(1)
      .then(response => {
        this.setState({
          guests: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  refreshList() {
    this.retrieveGuest();
    this.setState({
      currentGuest: null,
      currentIndex: -1
    });
  }

  setActiveGuest(guest, index) {
    this.setState({
      currentGuest: guest,
      currentIndex: index
    });
  }

  removeAllGuests() {
    GuestDataService.deleteAll()
      .then(response => {
        console.log(response.data);
        this.refreshList();
      })
      .catch(e => {
        console.log(e);
      });
  }

  searchGuest() {
    this.setState({
      currentGuest: null,
      currentIndex: -1
    });

    GuestDataService.findByTitle(this.state.searchGuest)
      .then(response => {
        this.setState({
          guests: response.data
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  render() {
    const { searchGuest, guests, currentGuest, currentIndex } = this.state;

    return (
      <div className="list row">
        <div className="col-md-8">
          <div className="input-group mb-3">
            <input
              type="text"
              className="form-control"
              placeholder="Search by title"
              value={searchGuest}
              onChange={this.onChangeSearchGuest}
            />
            <div className="input-group-append">
              <button
                className="btn btn-outline-secondary"
                type="button"
                onClick={this.searchGuest}
              >
                Search
              </button>
            </div>
          </div>
        </div>
        <div className="col-md-6">
          <h4>Tutorials List</h4>

          <ul className="list-group">
            {guests &&
              guests.map((guest, index) => (
                <li
                  className={
                    "list-group-item " +
                    (index === currentIndex ? "active" : "")
                  }
                  onClick={() => this.setActiveGuest(guests, index)}
                  key={index}
                >
                  {guest.name}
                </li>
              ))}
          </ul>

          <button
            className="m-3 btn btn-sm btn-danger"
            onClick={this.removeAllTutorials}
          >
            Remove All
          </button>
        </div>
        <div className="col-md-6">
          {currentGuest ? (
            <div>
              <h4>Tutorial</h4>
              <div>
                <label>
                  <strong>Title:</strong>
                </label>{" "}
                {currentGuest.name}
              </div>
              <div>
                <label>
                  <strong>Description:</strong>
                </label>{" "}
                {currentGuest.message}
              </div>

              <Link
                to={"/tutorials/" + currentGuest.id}
                className="badge badge-warning"
              >
                Edit
              </Link>
            </div>
          ) : (
            <div>
              <br />
              <p>Please click on a Tutorial...</p>
            </div>
          )}
        </div>
      </div>
    );
  }
}
