import React, { Component } from "react";
import TutorialDataService from "../services/tutorial.service";

export default class AddTutorial extends Component {
  constructor(props) {
    super(props);
    this.onChangeNome = this.onChangeNome.bind(this);
    this.onChangeMensagem = this.onChangeMensagem.bind(this);
    this.onChangeCategoria = this.onChangeCategoria.bind(this);
    this.onChangePeriodo = this.onChangePeriodo.bind(this);
    this.save = this.save.bind(this);
    this.new = this.new.bind(this);

    this.state = {
      id: null,
      nome: "",
      mensagem: "", 
      categoria: "",
      periodo: "",
      submitted: false
    };  
  }

  onChangeNome(e) {
    this.setState({
      nome: e.target.value
    });
  }

  onChangeMensagem(e) {
    this.setState({
      mensagem: e.target.value
    });
  }

  onChangeCategoria(e) {
    this.setState({
      categoria: e.target.value
    });
  }

  onChangePeriodo(e) {
    this.setState({
      periodo: e.target.value
    });
  }

  save() {
    var data = {
      nome: this.state.nome,
      mensagem: this.state.mensagem,
      categoria: this.state.categoria,
      periodo: this.state.periodo

    };

    TutorialDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,
          nome: response.data.name,
          mensagem: response.data.mensagem,
          categoria: response.data.categoria,
          periodo: response.data.periodo,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  new() {
    this.setState({
      id: null,
      nome: "",
      mensagem: "", 
      categoria: "",
      periodo: "",

      submitted: false
    });
  }

  render() {
    return (
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>You submitted successfully!</h4>
            <button className="btn btn-success" onClick={this.new}>
              Add
            </button>
          </div>
        ) : (

          <div>
            <div className="form-group">
              <label htmlFor="nome">Nome</label>
              <input
                type="text"
                className="form-control"
                id="nome"
                required
                value={this.state.nome}
                onChange={this.onChangeNome}
                name="nome"
              />
            </div>

            <div className="form-group">
              <label htmlFor="mensagem">Mensagem</label>
              <textarea
                type="text"
                className="form-control"
                id="mensagem"
                required
                value={this.state.mensagem}
                onChange={this.onChangeMensagem}
                name="mensagem"
              />
            </div>

            <div className="form-group">
              <label htmlFor="categoria">Categoria</label>
              <select  className="form-control" value={this.state.categoria} onChange={this.onChangeCategoria}>
                <option value="1">Laranja</option>
                <option value="2">Limão</option>
              </select>
            </div>

            <div className="form-group">
              <label htmlFor="periodo">Período</label>
              <input
                type="text"
                className="form-control"
                id="periodo"
                required
                value={this.state.periodo}
                onChange={this.onChangePeriodo}
                name="periodo"
              />
            </div>

            <button onClick={this.save} className="btn btn-success">
              Salvar
            </button>
          </div>
        )}
      </div>
    );
  }
}
