import React, { Component } from "react";
import GuestDataService from "../services/guest.service";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as yup from "yup";

export default class AddGuest extends Component {
  constructor(props) {
    super(props);
    this.onChangeNome = this.onChangeNome.bind(this);
    this.onChangeMensagem = this.onChangeMensagem.bind(this);
    this.onChangeCategoria = this.onChangeCategoria.bind(this);
    this.onChangePeriodo = this.onChangePeriodo.bind(this);
    this.save = this.save.bind(this);
    this.new = this.new.bind(this);

    const validationSchema = yup.object({
      nome: yup
        .string()
        .nome()
        .required("Informe o Nome"),
      categoria: yup
        .number()
        .required("Informe a Categoria")
        .positive()
        .min(1)
        .integer(),
        periodo: yup
        .number()
        .required("Informe o Período")
        .positive()
        .min(1)
        .integer()
    });


    this.state = {
      id: null,
      nome: "",
      mensagem: "", 
      categoria: 0,
      periodo: 0,
      submitted: false
    };  
  }

  onChangeNome(e) {
    this.setState({
      nome: e.target.value
    });
  }

  onChangeMensagem(e) {
    this.setState({
      mensagem: e.target.value
    });
  }

  onChangeCategoria(e) {
    this.setState({
      categoria: e.target.value
    });
  }

  onChangePeriodo(e) {
    this.setState({
      periodo: e.target.value
    });
  }

  save() {
    var data = {
      name: this.state.nome,
      message: this.state.mensagem,
      visitPeriod: parseInt(this.state.categoria),
      guestType: parseInt(this.state.periodo)

    };

    GuestDataService.create(data)
      .then(response => {
        this.setState({
          id: response.data.id,

          submitted: true
        });
        console.log(response.data);
      })
      .catch(e => {
        console.log(e);
      });
  }

  new() {
    this.setState({
      id: null,
      nome: "",
      mensagem: "", 
      categoria: 0,
      periodo: 0,

      submitted: false
    });
  }

  render() {
    return (
      <Formik
        initialValues={{ name: "", categoria: 0, periodo: 0 }}
        validationSchema={validationSchema}
      >
      <div className="submit-form">
        {this.state.submitted ? (
          <div>
            <h4>Check-in ok! </h4>
            <button className="btn btn-success" onClick={this.new}>
              Novo
            </button>
          </div>
        ) : (

          <div>
            <div className="form-group">
              <label htmlFor="nome">Nome</label>
              <input
                type="text"
                className="form-control"
                id="nome"
                required
                value={this.state.nome}
                onChange={this.onChangeNome}
                name="nome"
              />
              <ErrorMessage component="div" name="nome" />
            </div>

            <div className="form-group">
              <label htmlFor="mensagem">Mensagem</label>
              <textarea
                type="text"
                className="form-control"
                id="mensagem"
                required
                value={this.state.mensagem}
                onChange={this.onChangeMensagem}
                name="mensagem"
              />
            </div>

            <div className="form-group">
              <label htmlFor="categoria">Categoria</label>
              <select className="form-control" value={this.state.categoria} onChange={this.onChangeCategoria}>
                <option value={0}>-- Selecione --</option>
                <option value={1}>VISITANTE</option>
                <option value={2}>AVISO</option>
              </select>
              <ErrorMessage component="div" name="categoria" />
            </div>

            <div className="form-group">
              <label htmlFor="periodo">Período</label>
              <select className="form-control" value={this.state.periodo} onChange={this.onChangePeriodo}>
                <option value={0}>-- Selecione --</option>
                <option value={1}>MANHA</option>
                <option value={2}>TARDE</option>
                <option value={3}>NOITE</option>
              </select>
              <ErrorMessage component="div" name="periodo" />
            </div>

            <button onClick={this.save} className="btn btn-success">
              Salvar
            </button>
          </div>
        )}
      </div>
      </Formik>
    );
  }
}
